import React, { createContext, useEffect, useState } from 'react';
import all_product from '../components/assets/all_products';

export const ShoesContext = createContext(null);

const getDefaultCart = () => {
  let cart = {}
  for (let i = 0; i < all_product.length+1; i++) {
    cart[i] = 0;
  }
  return cart;
}

const ShoesContextProvider = (props) => {

  const [cartItems, setCartItems] = useState(getDefaultCart());
  const [all_product, setAll_Product] = useState([]);

  useEffect(() => {
    fetch('http://localhost:4000/allproducts')
    .then((response) => response.json())
    .then((data) => setAll_Product(data))

    if(localStorage.getItem('auth-token')) {
      fetch('http://localhost:4000/getcart', {
        method: 'POST',
        headers: {
          Accept: 'application/form-data',
          'auth-token':`${localStorage.getItem('auth-token')}`,
          'Content-Type':'application/json',
        },
        body:"",
      }).then((response) => response.json())
        .then((data) => setCartItems(data));
    }
  }, [])

  const addToCart = (itemId) => {
    setCartItems((prev) => ({...prev,[itemId]:prev[itemId]+1}))
    if(localStorage.getItem('auth-token')) {
        fetch('http://localhost:4000/addtocart', {
            method: 'POST',
            headers: {
                Accept: 'application/form-data',
                'auth-token':`${localStorage.getItem('auth-token')}`,
                'Content-Type':'application/json',
            },
            body:JSON.stringify({"itemId":itemId}),
        })
        .then((response) => response.json())
        .then((data) => console.log(data));
    }
};

 const removeFromCart = (itemId) => {
  setCartItems((prev) => ({...prev,[itemId]:prev[itemId]-1}));
  if(localStorage.getItem('auth-token')) {
    fetch('http://localhost:4000/removefromcart', {
      method: 'POST',
      headers: {
        Accept: 'application/form-data',
        'auth-token':`${localStorage.getItem('auth-token')}`,
        'Content-Type':'application/json',
      },
      body:JSON.stringify({"itemId":itemId}),
    })
    .then((response) => response.json())
    .then((data) => console.log(data))
  }
 };

  const getTotalCartAmount = () => {
    let totalAmount = 0;
    for(const item in cartItems) {
      if(cartItems[item]>0) {
        let itemInfo = all_product.find((product) => product.id===Number(item))
        totalAmount += itemInfo.new_price * cartItems[item];
      }
    }
    return totalAmount;
  }

  const getTotalCartItems = () => {
    let totalItem = 0;
    for(const item in cartItems) {
      if(cartItems[item]>0) {
        totalItem += cartItems[item];
      }
    }
    return totalItem;
  }

    const contextValue = { addToCart, cartItems, removeFromCart, getTotalCartAmount, getTotalCartItems, all_product };

  return (
    <ShoesContext.Provider value={contextValue}>
        {props.children}
    </ShoesContext.Provider>
  )
}

export default ShoesContextProvider;
